mapper.update:
update bbs
set title=#{title}, content=#{content}
where seq=#{seq}

update bbs
set del=1
where seq=#{seq}

mapper.select:
select count(*)
from readcount
where id=#{id} and category='bbs' and checkseq=#{checkseq}

mapper.update:
update bbs
set readcount=readcount+1
where seq=#{seq}

mapper.select:
select seq, id, content, wdate from bbscomment
where seq=#{seq}
order by wdate desc
